package com.example.makemytrip.splashScreen;

import androidx.appcompat.app.AppCompatActivity;

import android.content.Intent;
import android.os.Bundle;
import android.os.Handler;
import android.widget.ImageView;

import com.bumptech.glide.Glide;
import com.example.makemytrip.LoginOrSignup.LoginActivity;
import com.example.makemytrip.MainActivity;
import com.example.makemytrip.R;

public class SplashScreen extends AppCompatActivity {

    private Handler handler = new Handler();
    private ImageView mIvMakeMyTrip;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_splash_screen);
        mIvMakeMyTrip = findViewById(R.id.ivMakeMyTrip_SplashScreen);
        Glide.with(mIvMakeMyTrip).load("data:image/jpeg;base64,").into(mIvMakeMyTrip);
        handler.postDelayed(new Runnable() {
            @Override
            public void run() {
                Intent intent = new Intent(SplashScreen.this, LoginActivity.class);
                startActivity(intent);
                finish();
            }
        }, 3000);
    }
}